const tabButtons = document.querySelectorAll(".sk-tab-button"),
  tabs = document.querySelectorAll(".sk-tab-content");
tabButtons.forEach((t) => {
  t.addEventListener("click", () => {
    const e = t.getAttribute("data-tab");
    tabs.forEach((t) => {
      const a = t.id === e;
      t.setAttribute("aria-hidden", !a);
    }),
      tabButtons.forEach((t) => {
        const a = t.getAttribute("data-tab") === e;
        t.setAttribute("aria-checked", a);
      });
  });
});
